mapper.select:
SELECT a.pilmmSeq ,a.pilmmName ,(SELECT pilcdName FROM PILATESCODE WHERE 1=1 AND pilcgSeq=33 and pilcdSeq=a.pilmmGenderCd)AS pilmmGenderCd ,a.pilmmDob ,b.pilmpNumber ,e.pilpgName ,c.pilpyStartDate ,c.pilpyEndtDate ,a.pilmmMemberNumber ,(SELECT pilinName FROM PILATESINSTRUCTOR WHERE 1=1 AND pilinSeq= d.pilinSeq)AS pilinName ,c. pilpyDregisterDate ,(SELECT pilcdName FROM PILATESCODE WHERE 1=1 AND pilcgSeq=12 AND pilcdSeq= f.piljqQuestionCd)AS piljqQuestionCd ,f.piljqSeq
FROM PILATESMEMBER as a LEFT JOIN PILATESMEMBERPHONE AS b ON b.pilmmSeq=a.pilmmSeq LEFT JOIN PILATESPAYMENT AS c ON c.pilmmSeq=a.pilmmSeq LEFT JOIN PILATESPROGRAMINSTRUCTOR AS d ON d.pilpgiSeq=c.pilpgiSeq LEFT JOIN PILATESPROGRAM AS e ON e.pilpgSeq=d.pilpgSeq LEFT JOIN PILLATESMEMBERJOINQNA AS f ON f.pilmmSeq=a.pilmmSeq WHERE 1=1
AND a.pilmmDelNy = 0 ORDER BY pilmmSeq DESC limit #{startRnumForMysql}, #{rowNumToShow}

mapper.insert:
	insert into pilatesMember (
		pilmmId 
		,pilmmPassword
		,pilmmName
		,pilmmDelNy
	) values (
		#{pilmmId}
		,#{pilmmPassword}
		,#{pilmmName}
		,0
	)

SELECT last_insert_id()

mapper.update:
UPDATE
	PILATESMEMBER 
SET 
	pilmmName = #{pilmmName}
WHERE 1=1
	AND  pilmmSeq = #{pilmmSeq}
